'use strict';

var colors = require('colors');

var logger = exports;

var _silence = false;

logger.logger = function (options) {
    options = Object.assign({ silence: false }, options);
    _silence = options.silence;
};

logger.log = function (msg) {
    if (_silence != true) {
        console.log(msg);
    }
};

logger.warning = function (msg) {
    logger.log(colors.cyan(msg));
};

logger.success = function (msg) {
    logger.log(colors.green(msg));
};

logger.error = function (msg) {
    logger.log(colors.red(msg));
};