"use strict";

var utils = exports;
/**
 * squeezePropsArray
 * @param  Array objects
 * @param  string key
 * @return Array
 *
 * [{a:1}, {c:1, b:2, a:3}] => (a) => [{a:1}, {a:3}]
 * [{a:1}, {c:1, b:2, a:3}] => (b) => [{b:2}]
 * [{a:1}, {c:1, b:2, a:3}] => (d) => []
 */
utils.squeezePropsArray = function (objects, key) {
    objects = objects || [];
    var propsArray = [];
    for (var i = 0; i < objects.length; i++) {
        if (objects[i][key]) {
            propsArray.push(objects[i][key]);
        }
    }
    return propsArray;
};